css:

/* Force A8 dimensions (52x74mm) with zero margins */ 
@page { 
    size: 52mm, 37mm !important; 
    margin: 0mm !important; 
    padding: 0mm !important; 


}
.barcode {
    width: 50mm !important;
    height: 37mm !important; 
    margin: 0mm !important;
    padding: 1mm !important;

    transform: scale(1); /* Disable auto-scaling */
    font-size: 9pt; 
    font-family: 'Open Sans', sans-serif; 

        /* Prevent overflow */
      /*  overflow: hidden !important;*/
/* Include border/padding in dimensions */ 
font-size: 8pt; 
font-family: 'Open Sans', sans-serif; 
margin-left:1mm;
margin-top:1mm; 
margin-right:1mm;
margin-bottom:1mm

} 


.print-format {
    width: 51mm !important;
    height: 36mm !important; 
    margin: 0.5mm !important;
    padding: 0mm !important;
  /*  transform-origin: top left; */
    transform: scale(1); /* Disable auto-scaling */

    font-family: 'Open Sans', sans-serif; 

        /* Prevent overflow */
        overflow: hidden !important;
/* Include border/padding in dimensions */ 
/*position: absolute;*/
font-size: 9pt; 
font-family: 'Open Sans', sans-serif; 
margin-left:0mm;
margin-top:0mm; 
